import React,{useState} from 'react'
import DeletePopup from './DeletePopup'

const Task = ({task,remove,setRemove}) => {



    let start=0;
    let interval;

    const downHandler = () => {
        interval = setInterval(() => {

            if(start==10){
                clearInterval(interval);
                setRemove(true);
                start=0;
            }else{
            console.log(start);
            start+=1;
            }

        }, 100);
    }

    return(

        <div onMouseDown={downHandler} className="task">
            {remove&&<DeletePopup setRemove={setRemove}/>}
            <p>{task.text}</p>
        </div>

    )
}

export default Task
