import React from 'react'

const Task = ({task}) => {

    let start=0;

    const downHandler = () => {
        setTimeout(() => {
            start+=1;
            console.log(start)
        }, 10);
    }

    return(
        <div onMouseDown={downHandler} className="task">
            <p>{task.text}</p>
        </div>

    )
}

export default Task
