import React from 'react'

const Task = ({task}) => {

    let start=0;
    let interval;

    const downHandler = () => {
        interval = setInterval(() => {

            if(start==5)
                clearInterval(interval)
            console.log(start);
            start+=1;

        }, 100);
    }

    const upHandler = () => {
        clearInterval(interval)
        console.log(start)
    }

    return(
        <div onMouseDown={downHandler} onMouseUp={upHandler} className="task">
            <p>{task.text}</p>
        </div>

    )
}

export default Task
