import React from 'react'

const Task = ({task}) => {

    let start=0;

    const downHandler = () => {
        let interval = setInterval(() => {
            console.log(start);
            start+=1;
        }, 100);

        if(start===5)
            clearInterval(interval)
    }

    return(
        <div onMouseDown={downHandler} className="task">
            <p>{task.text}</p>
        </div>

    )
}

export default Task
