import React,{useState} from 'react'
import DeletePopup from './DeletePopup'

const Task = ({task}) => {


    const [remove, setRemove] = useState(false); 


    let start=0;
    let interval=true;

    const downHandler = () => {
        interval = setInterval(() => {
            if(start===10){
                setRemove(true);
                clearInterval(interval);
                return;
            }
            console.log(start);
            start+=1;
        }, 100);
    }

    const upHandler = () => {
        clearInterval(interval);

        console.log("add")
        start=0;
    }

    return(

        <div onMouseDown={downHandler} onMouseUp={upHandler} className="task">
            {/* {remove&&<DeletePopup setRemove={setRemove}/>} */}
            <p>{task.text}</p>
        </div>

    )
}

export default Task
